import React from 'react';
import { useMovies } from '../context/MovieContext';
import MovieGrid from '../components/MovieGrid';
import { TrendingUp, Star, Flame } from 'lucide-react';

const Home = () => {
  const { trendingMovies, popularMovies, topRatedMovies, loading } = useMovies();

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative h-96 bg-gradient-to-r from-dark-800 to-dark-900 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-primary-600/20 to-transparent"></div>
        <div className="relative z-10 flex items-center justify-center h-full">
          <div className="text-center space-y-4">
            <h1 className="text-5xl md:text-7xl font-bold text-white">
              Welcome to <span className="text-primary-500">CineSearch</span>
            </h1>
            <p className="text-xl text-dark-300 max-w-2xl mx-auto">
              Discover the latest movies, explore trending films, and find your next favorite
            </p>
          </div>
        </div>
      </section>

      {/* Content Sections */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 space-y-16">
        {/* Trending Movies */}
        <section>
          <div className="flex items-center space-x-2 mb-6">
            <TrendingUp className="h-6 w-6 text-primary-500" />
            <h2 className="text-3xl font-bold text-white">Trending This Week</h2>
          </div>
          <MovieGrid movies={trendingMovies} loading={loading} />
        </section>

        {/* Popular Movies */}
        <section>
          <div className="flex items-center space-x-2 mb-6">
            <Fire className="h-6 w-6 text-primary-500" />
            <h2 className="text-3xl font-bold text-white">Popular Movies</h2>
          </div>
          <MovieGrid movies={popularMovies} loading={loading} />
        </section>

        {/* Top Rated Movies */}
        <section>
          <div className="flex items-center space-x-2 mb-6">
            <Star className="h-6 w-6 text-primary-500" />
            <h2 className="text-3xl font-bold text-white">Top Rated</h2>
          </div>
          <MovieGrid movies={topRatedMovies} loading={loading} />
        </section>
      </div>
    </div>
  );
};

export default Home;
